import Button from '@mui/material/Button';
import swal from 'sweetalert'
import TextField from '@mui/material/TextField';
import { useState } from 'react';
import { getAllAdvertiser, login, signIn } from '../api';
import { useNavigate } from 'react-router-dom';
// import { useNavigate } from "react-router-dom"

export const Login = () => {
    const [user, setUser] = useState({});
    const [h, setH] = useState("הרשמה")
    const [erroe, setError] = useState({})
    const nav = useNavigate()
    const send = () => {
        if (user.email && user.password && user.phone) {
            login(user)
                .then(x => {
                    swal("ברוך הבא", "", "success");
                    console.log(x);

                    //ניתוב לדף הבית
                    //  nav('/homePage')
                })
                .catch(x => {
                    // swal("!!!!שגיאה ", `שגיאה מספר ${x.status.code}`, "error");
                    if (x.status == 404) {
                        setH("התחברות")
                        signIn(user).then(x => {
                            swal("ברוך הבא", "ההרשמה בוצעה בהצלחה", "success");
                            //ניתוב לדף הבית
                            nav('/homePage')
                        })
                            .catch(err => {
                                if (x.status = 300)
                                    swal("!!!!שגיאה ", ` הערכים שהכנסת אינם תקינים הכנס שנית`, "error");
                                //לברר מה הסטטוס הנכון
                                else
                                    swal("!!!!שגיאה ", ` ${err.message}`, "error");
                            })

                    }



                })
        }
        else {
            swal("!!!!שגיאה ", "מלא את כל השדות", "error");
        }

    }
    const checMail = () => {
        const numRegex =/^[\w-\.]+@([\w-]+\.)+[\w-]{2,4}$/
        if (!value.match(numRegex)) {
            setError({ ...errors, email: 'הכנס כתובת תקינה' })
        }
        else {
            setError({ ...errors, passemailword: '' })
        }
    }
    const checFhone = () => {
        const numRegex =/^[+]*[(]{0,1}[0-9]{1,4}[)]{0,1}[-\s\./0-9]*$/
        if (!value.match(numRegex)) {
            setError({ ...errors, phone: 'הכנס כתובת תקינה' })
        }
        else {
            setError({ ...errors, phone: '' })
        }
    }
    return <>
        <div className='login'>
            <h2 id="h4">{h}</h2>
            <TextField id="outlined-basic" label=" הכנס אימייל" variant="outlined" onBlur={e => { setUser({ ...user, email: e.target.value }) }} onChange={e => { checMail(e.target.value) }} />
            <p className="error"></p>
            <TextField id="outlined-basic" label=" הכנס סיסמה" variant="outlined" onBlur={e => { setUser({ ...user, password: e.target.value }) }} />
            <p className="error"> </p>
            <TextField id="outlined-basic" label=" הכנס  טלפון" variant="outlined" onBlur={e => { setUser({ ...user, phone: e.target.value }) }} onChange={e => { checFhone(e.target.value) }} />
            <p className="error"></p>
            <TextField id="outlined-basic" label=" הכנס  טלפון נוסף" variant="outlined" onBlur={e => { setUser({ ...user, phone: e.target.value }) }} onChange={e => { checFhone(e.target.value) }}/>
            <p className="error"></p>
            <Button className='buto' variant="contained" onClick={send}>
                שליחה
            </Button>
        </div>

    </>
}
